import { useState, useRef } from "react";

const SimpleInput = (props) => {
  const nameInputRef = useRef();
  const [entredName, setEntredName] = useState('');
  const [entredNameIsValide, setEntredNameIsValide] = useState(true)

  const nameInputChangeHandler = (event) => {
    setEntredName(event.target.value)
  };

  const formSubmissionHandler = (event) => {
    event.preventDefault();

    if(entredName.trim() === ''){
      setEntredNameIsValide(false)
      return;
    };
    setEntredNameIsValide(true);

    console.log(entredName);
    const entredValue = nameInputRef.current.value;
    console.log(entredValue);

    // nameInputRef.current.value = ""; - NOT ideal, DO NOT manipulate the DOM 
    setEntredName("");
  };

  const nameInputClasses = entredNameIsValide ? 'form-control' : "form-control invalid"

  return (
    <form onSubmit={formSubmissionHandler}>
      <div className={nameInputClasses}>
        <label htmlFor='name'>Your Name</label>
        <input
          ref={nameInputRef}
          type='text'
          id='name'
          onChange={nameInputChangeHandler}
          value={entredName}
        />
        {!entredNameIsValide && <p className="error-text">Name must not be empty! </p>}
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
