import { useState } from "react";

const SimpleInput = (props) => {
  // const nameInputRef = useRef();
  const [entredName, setEntredName] = useState('');
  const [entredNameTouched, setEntredNameTouched] = useState(false);

  const [entredEmail, setEntredEmail] = useState('');
  const [entredEmailTouched, setEntredEmailTouched] = useState(false);

  const entredNameIsValide = entredName.trim() !== "";
  const nameInputIsInvalide = !entredNameIsValide && entredNameTouched

  const entredEmailIsValide = entredEmail.trim() !== "" && /\S+@\S+\.\S+/.test(entredEmail);
  const emailInputIsInvalide = !entredEmailIsValide && entredEmailTouched;

  let formIsValide = false;

  if (entredNameIsValide && entredEmailIsValide) {
    formIsValide = true;
  }

  const nameInputChangeHandler = (event) => {
    setEntredName(event.target.value)
  };

  const emailInputChangeHandler = (event) => {
    setEntredEmail(event.target.value)
  };

  const nameInputBlurHandler = () => {
    setEntredNameTouched(true);
  };

  const emailInputBlurHandler = () => {
    setEntredEmailTouched(true);
  }

  const formSubmissionHandler = (event) => {
    event.preventDefault();

    setEntredNameTouched(true);
    setEntredEmailTouched(true);

    if (!entredNameIsValide && !entredEmailIsValide) {
      return;
    };

    console.log(entredName);
    console.log(entredEmail);
    // const entredValue = nameInputRef.current.value;
    // console.log(entredValue);

    // nameInputRef.current.value = ""; - NOT ideal, DO NOT manipulate the DOM 
    setEntredName("");
    setEntredNameTouched(false);

    setEntredEmail('');
    setEntredEmailTouched(false);
  };

  const nameInputClasses = nameInputIsInvalide ? 'form-control invalid' : "form-control";
  const emailInputClasses = emailInputIsInvalide ? 'form-control invalid' : "form-control";

  return (
    <form onSubmit={formSubmissionHandler}>
      <div className={nameInputClasses}>
        <label htmlFor='name'>Your Name</label>
        <input
          // ref={nameInputRef}
          type='text'
          id='name'
          onChange={nameInputChangeHandler}
          onBlur={nameInputBlurHandler}
          value={entredName}
        />
        {nameInputIsInvalide && <p className="error-text">Name must not be empty! </p>}
      </div>
      <div className={emailInputClasses}>
        <label htmlFor='email'>Your E-mail</label>
        <input
          type='email'
          id='email'
          onChange={emailInputChangeHandler}
          onBlur={emailInputBlurHandler}
          value={entredEmail}
        />
        {emailInputIsInvalide && <p className="error-text">Please enter a valide email!</p>}
      </div>
      <div className="form-actions">
        <button disabled={!formIsValide}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
